import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function ExecutiveSearch({ executives, selectedExecutives, onAdd, onRemove }) {
  const [search, setSearch] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);

  const filteredExecutives = executives.filter(
    (exec) =>
      exec.name.toLowerCase().includes(search.toLowerCase()) &&
      !selectedExecutives.find((selected) => selected.id === exec.id)
  );

  const handleAdd = (executive) => {
    onAdd(executive);
    setSearch('');
    setShowSuggestions(false);
  };

  return (
    <>
      <search>
        <input
          type="search"
          placeholder="Type to search executives..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onFocus={() => setShowSuggestions(true)}
          aria-label="Search executives"
          aria-autocomplete="list"
          aria-expanded={
            showSuggestions && search && filteredExecutives.length > 0
          }
        />

        {showSuggestions && search && filteredExecutives.length > 0 && (
          <>
            <ul className="suggestions-dropdown" role="listbox">
              {filteredExecutives.map((exec) => (
                <li
                  key={exec.id}
                  onClick={() => handleAdd(exec)}
                  role="option"
                  tabIndex={0}
                  onKeyDown={(e) => e.key === 'Enter' && handleAdd(exec)}
                >
                  {exec.name}
                </li>
              ))}
            </ul>
            <button
              className="overlay"
              onClick={() => setShowSuggestions(false)}
              aria-label="Close suggestions"
              type="button"
            />
          </>
        )}
      </search>

      {selectedExecutives.length > 0 && (
        <output aria-label="Selected executives">
          <strong>Selected executives:</strong>
          <ul className="executive-chips">
            {selectedExecutives.map((exec) => (
              <li key={exec.id} className="chip">
                {exec.name}
                <button
                  type="button"
                  onClick={() => onRemove(exec.id)}
                  aria-label={`Remove ${exec.name}`}
                  className="remove-btn"
                >
                  Ã—
                </button>
              </li>
            ))}
          </ul>
        </output>
      )}
    </>
  );
}

function AddCSO() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    executives: [],
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState({ type: null, message: null });
  const [executives, setExecutives] = useState([]);
  const [selectedExecutives, setSelectedExecutives] = useState([]);

  useEffect(() => {
    fetchExecutives();
  }, []);

  const fetchExecutives = async () => {
    try {
      const { data: execData, error: execError } = await supabase
        .from('executive')
        .select('id, "student/staff_number"');

      if (execError) throw execError;

      if (execData?.length > 0) {
        const studentNumbers = execData
          .map((exec) => exec['student/staff_number'])
          .filter(Boolean);

        if (studentNumbers.length > 0) {
          const { data: profileData, error: profileError } = await supabase
            .from('profiles')
            .select('id, full_name')
            .in('id', studentNumbers);

          if (profileError) throw profileError;

          const executivesWithNames = execData.map((exec) => {
            const profile = profileData?.find(
              (p) => p.id === exec['student/staff_number']
            );
            return {
              id: exec.id,
              student_staff_number: exec['student/staff_number'],
              name: profile?.full_name || 'Unknown Executive',
            };
          });

          setExecutives(executivesWithNames);
        }
      }
    } catch (err) {
      console.error('Error fetching executives:', err);
      setStatus({ type: 'error', message: 'Failed to load executives' });
    }
  };

  const handleAddExecutive = (executive) => {
    setSelectedExecutives([...selectedExecutives, executive]);
    setFormData((prev) => ({
      ...prev,
      executives: [...prev.executives, executive.id],
    }));
  };

  const handleRemoveExecutive = (executiveId) => {
    setSelectedExecutives((prev) =>
      prev.filter((exec) => exec.id !== executiveId)
    );
    setFormData((prev) => ({
      ...prev,
      executives: prev.executives.filter((id) => id !== executiveId),
    }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      setStatus({ type: 'error', message: 'Please select an image file' });
      return;
    }
    if (file.size > 5 * 1024 * 1024) {
      setStatus({ type: 'error', message: 'File size must be less than 5MB' });
      return;
    }
    setLogoFile(file);
    setStatus({ type: null, message: null });
  };

  const uploadLogo = async (file) => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Date.now()}-${Math.random()}.${fileExt}`;

    const { data, error } = await supabase.storage
      .from('cso-logos') // Use the new bucket
      .upload(fileName, file);

    if (error) throw error;

    const {
      data: { publicUrl },
    } = supabase.storage.from('cso-logos').getPublicUrl(fileName);

    return publicUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setStatus({ type: null, message: null });

    try {
      if (!formData.name) throw new Error('CSO name is required');
      if (!formData.cluster) throw new Error('Cluster selection is required');

      // Upload logo if selected
      let logo_url = null;
      if (logoFile) {
        try {
          logo_url = await uploadLogo(logoFile);
          console.log('Logo uploaded successfully:', logo_url);
        } catch (uploadError) {
          console.error('Logo upload error:', uploadError);
          throw new Error(`Failed to upload logo: ${uploadError.message}`);
        }
      }

      // Insert into cso table
      console.log('Inserting into cso table:', {
        name: formData.name,
        description: formData.description,
        cluster: formData.cluster,
        subscription: formData.subscription,
        logo_url,
      });

      const { data: csoData, error: csoError } = await supabase
        .from('cso')
        .insert([
          {
            name: formData.name,
            description: formData.description,
            cluster: formData.cluster,
            subscription: formData.subscription,
            logo_url,
          },
        ])
        .select()
        .single();

      if (csoError) {
        console.error('CSO table insert error:', csoError);
        throw new Error(`Failed to create CSO: ${csoError.message}`);
      }

      console.log('CSO created successfully:', csoData);

      // Link executives if any are selected
      if (formData.executives.length > 0) {
        const executivesToLink = formData.executives.map((execId) => ({
          cso_id: csoData.id,
          exec_id: execId,
          portfolio: 'Member',
          can_post: true,
          start_date: new Date().toISOString().split('T')[0],
        }));

        console.log('Inserting into cso_exec table:', executivesToLink);

        const { error: linkError } = await supabase
          .from('cso_exec')
          .insert(executivesToLink);

        if (linkError) {
          console.error('CSO_EXEC table insert error:', linkError);

          // Attempt to delete the CSO since executive linking failed
          try {
            await supabase.from('cso').delete().eq('id', csoData.id);
            console.log(
              'Rolled back CSO creation due to executive linking failure'
            );
          } catch (deleteError) {
            console.error('Failed to rollback CSO creation:', deleteError);
          }

          throw new Error(`Failed to link executives: ${linkError.message}`);
        }

        console.log('Executives linked successfully');
      }

      // Success
      setStatus({ type: 'success', message: 'CSO created successfully!' });

      // Reset form
      setFormData({
        name: '',
        description: '',
        cluster: '',
        subscription: '',
        executives: [],
      });
      setSelectedExecutives([]);
      setLogoFile(null);

      const fileInput = document.getElementById('logo-input');
      if (fileInput) fileInput.value = '';

      // Navigate after success
      setTimeout(() => navigate('/CSO'), 2000);
    } catch (err) {
      console.error('Error in handleSubmit:', err);
      setStatus({
        type: 'error',
        message:
          err.message || 'An unexpected error occurred. Please try again.',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="add-cso-container">
      <h1>Add a club, society, or organization</h1>

      {status.message && (
        <output
          role="alert"
          className={`status-message ${status.type}`}
          aria-live="polite"
        >
          {status.message}
        </output>
      )}

      <form onSubmit={handleSubmit} className="cso-form">
        <label>
          Logo
          <input
            id="logo-input"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            aria-describedby="logo-help"
          />
          <small id="logo-help">Upload an image file (max 5MB)</small>
        </label>

        <label>
          CSO Name{' '}
          <abbr title="required" aria-label="required">
            *
          </abbr>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            aria-required="true"
          />
        </label>

        <label>
          Description
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
          />
        </label>

        <label>
          Cluster{' '}
          <abbr title="required" aria-label="required">
            *
          </abbr>
          <select
            name="cluster"
            value={formData.cluster}
            onChange={handleChange}
            required
            aria-required="true"
          >
            <option value="">Select a cluster...</option>
            <option value="political">Political Cluster</option>
            <option value="academic">Academic Cluster</option>
            <option value="social">Social Cluster</option>
            <option value="cultural">Cultural Cluster</option>
            <option value="religious">Religious Cluster</option>
            <option value="sport">WITS Sport</option>
            <option value="council">School Council</option>
          </select>
        </label>

        <fieldset>
          <legend>Subscription Required</legend>
          <label>
            <input
              type="radio"
              name="subscription"
              value="no"
              checked={formData.subscription === 'no'}
              onChange={handleChange}
            />
            No
          </label>
          <label>
            <input
              type="radio"
              name="subscription"
              value="yes"
              checked={formData.subscription === 'yes'}
              onChange={handleChange}
            />
            Yes
          </label>
        </fieldset>

        <fieldset>
          <legend>Executive(s)</legend>
          <ExecutiveSearch
            executives={executives}
            selectedExecutives={selectedExecutives}
            onAdd={handleAddExecutive}
            onRemove={handleRemoveExecutive}
          />
        </fieldset>

        <menu className="form-actions">
          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? 'Creating CSO...' : 'Add CSO'}
          </button>
          <button
            type="button"
            onClick={() => navigate('/CSO')}
            className="btn btn-secondary"
          >
            Cancel
          </button>
        </menu>
      </form>
    </main>
  );
}

export default AddCSO;
