import React, { useState } from 'react';
import { supabase } from '../supabaseClient'; // adjust the path if needed

function AddCSO({ students = [] }) {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    exec_id: '',
    executives: []
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleExecutivesChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
    setFormData({ ...formData, executives: selectedOptions });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        setError('Please select an image file');
        return;
      }
      if (file.size > 5 * 1024 * 1024) {
        setError('File size must be less than 5MB');
        return;
      }
      setLogoFile(file);
      setError(null);
    }
  };

  const uploadLogo = async (file) => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Math.random()}.${fileExt}`;
    const filePath = `cso-logos/${fileName}`;

    const { data, error } = await supabase.storage
      .from('logos')
      .upload(filePath, file);

    if (error) {
      throw error;
    }

    const { data: { publicUrl } } = supabase.storage
      .from('logos')
      .getPublicUrl(filePath);

    return publicUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      if (!formData.name) {
        throw new Error('CSO name is required');
      }
      if (!formData.cluster) {
        throw new Error('Cluster selection is required');
      }

      let logo_url = null;

      if (logoFile) {
        logo_url = await uploadLogo(logoFile);
      }

      const { data: csoData, error: csoError } = await supabase
        .from('cso')
        .insert([
          {
            name: formData.name,
            description: formData.description,
            cluster: formData.cluster,
            subscription: formData.subscription,
            exec_id: formData.exec_id || null,
            logo_url: logo_url
          }
        ])
        .select()
        .single();

      if (csoError) throw csoError;

      console.log("CSO created:", csoData);

      if (formData.executives.length > 0) {
        const executivesToLink = formData.executives.map(execId => ({
          cso_id: csoData.id,
          exec_id: execId,
          portfolio: "Member",
          can_post: true
        }));

        const { error: linkError } = await supabase
          .from('cso_executives')
          .insert(executivesToLink);

        if (linkError) throw linkError;
      }

      setSuccess(true);
      setFormData({
        name: '',
        description: '',
        cluster: '',
        subscription: '',
        exec_id: '',
        executives: []
      });
      setLogoFile(null);
      document.getElementById('logo-input').value = '';

    } catch (err) {
      setError(err.message);
      console.error("Error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-3 p-4">
      {error && <p style={{color: 'red'}}>Error: {error}</p>}
      {success && <p style={{color: 'green'}}>CSO created successfully!</p>}

      <label>
        Logo
        <input 
          id="logo-input"
          type="file" 
          accept="image/*"
          onChange={handleFileChange} 
        />
      </label><br />

      <label>
        CSO Name
        <input 
          type="text" 
          name="name" 
          value={formData.name} 
          onChange={handleChange} 
          required
        />
      </label><br />

      <label>
        Description
        <input 
          type="text" 
          name="description" 
          value={formData.description} 
          onChange={handleChange} 
        />
      </label><br />

      <label>
        Cluster
        <select 
          name="cluster" 
          value={formData.cluster} 
          onChange={handleChange}
          required
        >
          <option value="">Select a cluster...</option>
          <option value="political">Political Cluster</option>
          <option value="academic">Academic Cluster</option>
          <option value="social">Social Cluster</option>
          <option value="cultural">Cultural Cluster</option>
          <option value="religious">Religious Cluster</option>
          <option value="sport">WITS Sport</option>
          <option value="council">School Council</option>
        </select>
      </label><br />

      <label>
        Subscription
        <label>
          <input 
            type="radio" 
            name="subscription" 
            value="no" 
            onChange={handleChange} 
          />
          No
        </label>
        <label>
          <input 
            type="radio" 
            name="subscription" 
            value="yes" 
            onChange={handleChange} 
          />
          Yes
        </label>
      </label><br />

      <label>
        Primary Executive
        <select
          name="exec_id"
          value={formData.exec_id}
          onChange={handleChange}
        >
          <option value="">Select primary executive...</option>
          {students && students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.name || 'Unknown Student'}
            </option>
          ))}
        </select>
      </label><br />

      <label>
        Additional Executives
        <select
          multiple
          name="executives"
          value={formData.executives}
          onChange={handleExecutivesChange}
        >
          {students && students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.name || 'Unknown Student'}
            </option>
          ))}
        </select>
      </label><br />

      <button type="submit" disabled={loading}>
        {loading ? 'Creating CSO...' : 'Add CSO'}
      </button>
    </form>
  );
}

export default AddCSO;
