import React, { useState, useEffect } from "react";
import { supabase } from "../supabaseClient";
import '../styles/ExecPost.css';

export default function ExecPost({ entityId }) {
  const [caption, setCaption] = useState("");
  const [file, setFile] = useState(null);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch logged-in user
  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);
    };
    fetchUser();

    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
    });

    return () => listener.subscription.unsubscribe();
  }, []);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleSubmit = async () => {
    if (!user) return alert("You must be logged in to post.");
    if (!caption.trim() && !file) return alert("Please add a caption or select a file.");

    setLoading(true);

    try {
      let mediaUrl = null;
      let mediaType = null;

      // Only process file if one was selected
      if (file) {
        // 1. Upload file to Supabase Storage
        const fileExt = file.name.split(".").pop();
        const filePath = `${user.id}/${Date.now()}.${fileExt}`;

        const { error: uploadError } = await supabase.storage
          .from("media")
          .upload(filePath, file);

        if (uploadError) throw uploadError;

        const { data } = supabase.storage.from("media").getPublicUrl(filePath);
        mediaUrl = data.publicUrl;

        mediaType = file.type.startsWith("image")
          ? "image"
          : file.type.startsWith("video")
          ? "video"
          : "audio";
      }

      // 2. Insert post with entityId
      const { error: insertError } = await supabase.from("posts").insert([
        {
          caption: caption.trim(),
          media_url: mediaUrl,
          media_type: mediaType,
          cso_id: entityId
        }
      ]);

      if (insertError) throw insertError;

      alert("Post created successfully!");
      setCaption("");
      setFile(null);
      // Instead of reloading the page, you might want to use a state update
      window.location.reload(); // Refresh to show new post

    } catch (err) {
      console.error(err);
      alert("Error creating post: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleClearFile = () => {
    setFile(null);
    // Reset the file input
    const fileInput = document.querySelector('.post-file-input');
    if (fileInput) fileInput.value = '';
  };

  return (
    <div className="exec-post">
      <h3>Create Post</h3>

      <textarea
        placeholder="Write your caption... (optional if adding a file)"
        value={caption}
        onChange={(e) => setCaption(e.target.value)}
        rows={4}
        className="post-caption-input"
      />

      <div className="file-input-container">
        <label htmlFor="file-upload" className="file-upload-label">
          <span className="upload-icon">üìÅ</span>
          {file ? "Change File" : "Add File (optional)"}
        </label>
        <input 
          id="file-upload"
          type="file" 
          onChange={handleFileChange} 
          className="post-file-input"
        />
        
        {file && (
          <div className="file-preview">
            <span className="file-name">{file.name}</span>
            <button 
              type="button" 
              onClick={handleClearFile}
              className="clear-file-button"
            >
              √ó
            </button>
          </div>
        )}
      </div>

      <button
        onClick={handleSubmit}
        disabled={loading}
        className="post-submit-button"
      >
        {loading ? "Posting..." : "Create Post"}
      </button>
    </div>
  );
}
