import React, { useState, useEffect } from "react";
import { supabase } from "../supabaseClient";

export default function CSOPage() {
  const [caption, setCaption] = useState("");
  const [file, setFile] = useState(null);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [logo, setLogo] = useState(null);
  const [name, setName] = useState("");
  const [posts, setPosts] = useState([]);
  const [loadingPosts, setLoadingPosts] = useState(false);

  // Fetch logged-in user
  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);
    };
    fetchUser();

    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
    });

    return () => listener.subscription.unsubscribe();
  }, []);

  useEffect(() => {
    const fetchCSO = async () => {
      if (!user) return;

      try {
        const { data: executive, error: execError } = await supabase
          .from("executive")
          .select("id")
          .eq("id", user.id)
          .single();

        if (execError) {
          console.error("Error fetching executive:", execError);
          return;
        }

        const { data: csoExec, error: csoExecError } = await supabase
          .from("cso_exec")
          .select("cso_id")
          .eq("exec_id", executive.id)
          .single();

        if (csoExecError) {
          console.error("Error fetching CSO association:", csoExecError);
          return;
        }

        const { data: cso, error: csoError } = await supabase
          .from("cso")
          .select("name, logo_url")
          .eq("id", csoExec.cso_id)
          .single();

        if (csoError) {
          console.error("Error fetching CSO details:", csoError);
          return;
        }

        setName(cso.name);
        setLogo(cso.logo_url);
      } catch (err) {
        console.error("Error in fetchCSO:", err);
      }
    };

    fetchCSO();
  }, [user]);

  const fetchPosts = async () => {
    setLoadingPosts(true);
    try {
      const { data, error } = await supabase
        .from("posts")
        .select("*")
        .order("created_at", { ascending: false });

      if (error) {
        console.error("Error fetching posts:", error);
      } else {
        setPosts(data || []);
      }
    } catch (err) {
      console.error("Error in fetchPosts:", err);
    } finally {
      setLoadingPosts(false);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleSubmit = async () => {
    if (!user) return alert("You must be logged in to post.");
    if (!file) return alert("Please select a file.");

    setLoading(true);

    try {
      const fileExt = file.name.split(".").pop();
      const filePath = `${user.id}/${Date.now()}.${fileExt}`;

      const { error: uploadError } = await supabase.storage
        .from("media")
        .upload(filePath, file);

      if (uploadError) throw uploadError;

      const { data } = supabase.storage.from("media").getPublicUrl(filePath);
      const mediaUrl = data.publicUrl;

      const mediaType = file.type.startsWith("image")
        ? "image"
        : file.type.startsWith("video")
        ? "video"
        : "audio";

      const { error: insertError } = await supabase.from("posts").insert([
        {
          caption,
          media_url: mediaUrl,
          media_type: mediaType
        }
      ]);

      if (insertError) throw insertError;

      alert("Post created successfully!");
      setCaption("");
      setFile(null);
      fetchPosts();

    } catch (err) {
      console.error(err);
      alert("Error creating post: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div style={{ maxWidth: "600px", margin: "1rem auto", padding: "1rem", border: "1px solid #ccc", borderRadius: "8px" }}>
        {(name || logo) && (
          <div style={{ 
            marginBottom: "2rem", 
            padding: "1rem", 
            backgroundColor: "#f8f9fa", 
            borderRadius: "8px",
            display: "flex",
            alignItems: "center",
            gap: "1rem"
          }}>
            {logo && (
              <img 
                src={logo} 
                alt={`${name} logo`}
                style={{ 
                  width: "60px", 
                  height: "60px", 
                  objectFit: "contain",
                  borderRadius: "8px"
                }} 
              />
            )}
            {name && (
              <div>
                <h3 style={{ margin: 0, color: "#333" }}>{name}</h3>
                <p style={{ margin: 0, fontSize: "0.9rem", color: "#666" }}>Civil Society Organization</p>
              </div>
            )}
          </div>
        )}

        <h2>Create Post</h2>

        <textarea
          placeholder="Write your caption..."
          value={caption}
          onChange={(e) => setCaption(e.target.value)}
          rows={4}
          style={{ width: "100%", marginBottom: "1rem", padding: "0.5rem" }}
        />

        <input type="file" onChange={handleFileChange} style={{ marginBottom: "1rem" }} />

        <button
          onClick={handleSubmit}
          disabled={loading}
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          {loading ? "Posting..." : "Post"}
        </button>
      </div>

      <div>
        <h2>Posts</h2>
        {loadingPosts ? (
          <p>Loading posts...</p>
        ) : posts.length === 0 ? (
          <p>No posts yet.</p>
        ) : (
          <div>
            {posts.map((post) => (
              <div key={post.id}>
                {post.caption && <p>{post.caption}</p>}
                
                {post.media_url && (
                  <div>
                    {post.media_type === "image" && (
                      <img src={post.media_url} alt="Post content" />
                    )}
                    {post.media_type === "video" && (
                      <video controls>
                        <source src={post.media_url} />
                      </video>
                    )}
                    {post.media_type === "audio" && (
                      <audio controls>
                        <source src={post.media_url} />
                      </audio>
                    )}
                  </div>
                )}
                
                <p>{new Date(post.created_at).toLocaleString()}</p>
                <hr />
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
