import React, { useState } from 'react';

function AddCSO() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    executives: []
  });

  // Dummy student list (replace with real data or API)
  const students = [
    "Alice Johnson", "Bob Smith", "Charlie Brown",
    "David Lee", "Eva Green", "Fatima Khan"
  ];

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData({
      ...formData,
      [name]: type === "radio" ? value : value
    });
  };

  const handleExecutivesChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
    setFormData({ ...formData, executives: selectedOptions });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    // You can send this to your backend
  };

  return (

    <form onSubmit={handleSubmit} className="flex flex-col gap-3 p-4">
      <label>
        Logo
        <input type="file" name="logo" />
      </label>

      <label>
        CSO Name
        <input type="text" name="name" value={formData.name} onChange={handleChange} />
      </label>

      <label>
        Description
        <input type="text" name="description" value={formData.description} onChange={handleChange} />
      </label>

      <label>
        Cluster
        <select name="cluster" value={formData.cluster} onChange={handleChange}>
          <option value="">Select a cluster</option>
          <option value="political">Political Cluster</option>
          <option value="academic">Academic Cluster</option>
          <option value="social">Social Cluster</option>
          <option value="cultural">Cultural Cluster</option>
          <option value="religious">Religious Cluster</option>
          <option value="sport">WITS Sport</option>
          <option value="council">School Council</option>
        </select>
      </label><br></br>

      <label>
        Subscription
        <label>
          <input type="radio" name="subscription" value="no" onChange={handleChange} />
          No
        </label>
        <label>
          <input type="radio" name="subscription" value="yes" onChange={handleChange} />
          Yes
        </label>
      </label>

      <label>
        Executives (select multiple)
        <select
          multiple
          name="executives"
          value={formData.executives}
          onChange={handleExecutivesChange}
        >
          {students.map((student, index) => (
            <option key={index} value={student}>{student}</option>
          ))}
        </select>
      </label>

      <button type="submit">Add CSO</button>
    </form>
  );
}

export default AddCSO;
