import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function AddCSO() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    executives: [],
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [executives, setExecutives] = useState([]);

  // Fetch executives from the database
  useEffect(() => {
    fetchExecutives();
  }, []);

  const fetchExecutives = async () => {
    try {
      // First get executives
      const { data: execData, error: execError } = await supabase
        .from('executive')
        .select('id, student/staff_');

      if (execError) throw execError;

      // Then get profiles for those executives to get their names
      if (execData && execData.length > 0) {
        const studentNumbers = execData
          .map((exec) => exec['student/staff_'])
          .filter(Boolean);

        const { data: profileData, error: profileError } = await supabase
          .from('profiles')
          .select('id, full_name')
          .in('id', studentNumbers);

        if (profileError) throw profileError;

        // Combine executive data with profile names
        const executivesWithNames = execData.map((exec) => {
          const profile = profileData?.find(
            (p) => p.id === exec['student/staff_']
          );
          return {
            ...exec,
            name: profile?.full_name || 'Unknown Executive',
          };
        });

        setExecutives(executivesWithNames);
      }
    } catch (err) {
      console.error('Error fetching executives:', err);
      setError('Failed to load executives');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleExecutivesChange = (e) => {
    const selectedOptions = Array.from(
      e.target.selectedOptions,
      (option) => option.value
    );
    setFormData({ ...formData, executives: selectedOptions });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        setError('Please select an image file');
        return;
      }
      if (file.size > 5 * 1024 * 1024) {
        setError('File size must be less than 5MB');
        return;
      }
      setLogoFile(file);
      setError(null);
    }
  };

  const uploadLogo = async (file) => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Math.random()}.${fileExt}`;
    const filePath = `cso-logos/${fileName}`;

    const { data, error } = await supabase.storage
      .from('logos')
      .upload(filePath, file);

    if (error) {
      throw error;
    }

    const {
      data: { publicUrl },
    } = supabase.storage.from('logos').getPublicUrl(filePath);

    return publicUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      if (!formData.name) {
        throw new Error('CSO name is required');
      }
      if (!formData.cluster) {
        throw new Error('Cluster selection is required');
      }

      let logo_url = null;

      if (logoFile) {
        logo_url = await uploadLogo(logoFile);
      }

      // Insert CSO
      const { data: csoData, error: csoError } = await supabase
        .from('cso')
        .insert([
          {
            name: formData.name,
            description: formData.description,
            cluster: formData.cluster,
            subscription: formData.subscription,
            logo_url: logo_url,
          },
        ])
        .select()
        .single();

      if (csoError) throw csoError;

      console.log('CSO created:', csoData);

      // Link executives to CSO through cso_exec table
      if (formData.executives.length > 0) {
        const executivesToLink = formData.executives.map((execId) => ({
          cso_id: csoData.id,
          exec_id: execId,
          portfolio: 'Member', // Default portfolio
          can_post: true,
          start_date: new Date().toISOString().split('T')[0], // Today's date
        }));

        const { error: linkError } = await supabase
          .from('cso_exec')
          .insert(executivesToLink);

        if (linkError) throw linkError;
      }

      setSuccess(true);
      // Reset form
      setFormData({
        name: '',
        description: '',
        cluster: '',
        subscription: '',
        executives: [],
      });
      setLogoFile(null);

      // Reset file input
      const fileInput = document.getElementById('logo-input');
      if (fileInput) fileInput.value = '';

      // Navigate after a short delay to show success message
      setTimeout(() => {
        navigate('/CSO');
      }, 2000);
    } catch (err) {
      setError(err.message);
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const cancel = () => {
    navigate('/CSO');
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-3 p-4">
      <h1>Add a club, society, or organization</h1>
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {success && <p style={{ color: 'green' }}>CSO created successfully!</p>}

      <label>
        Logo
        <input
          id="logo-input"
          type="file"
          accept="image/*"
          onChange={handleFileChange}
        />
      </label>
      <br />

      <label>
        CSO Name
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </label>
      <br />

      <label>
        Description
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          rows="3"
        />
      </label>
      <br />

      <label>
        Cluster
        <select
          name="cluster"
          value={formData.cluster}
          onChange={handleChange}
          required
        >
          <option value="">Select a cluster...</option>
          <option value="political">Political Cluster</option>
          <option value="academic">Academic Cluster</option>
          <option value="social">Social Cluster</option>
          <option value="cultural">Cultural Cluster</option>
          <option value="religious">Religious Cluster</option>
          <option value="sport">WITS Sport</option>
          <option value="council">School Council</option>
        </select>
      </label>
      <br />

      <label>
        Subscription Required
        <div>
          <label>
            <input
              type="radio"
              name="subscription"
              value="no"
              checked={formData.subscription === 'no'}
              onChange={handleChange}
            />
            No
          </label>
          <label style={{ marginLeft: '1rem' }}>
            <input
              type="radio"
              name="subscription"
              value="yes"
              checked={formData.subscription === 'yes'}
              onChange={handleChange}
            />
            Yes
          </label>
        </div>
      </label>
      <br />

      <label>
        Executive(s)
        <select
          multiple
          value={formData.executives}
          onChange={handleExecutivesChange}
          size="5"
          style={{ width: '100%' }}
        >
          {executives.map((executive) => (
            <option key={executive.id} value={executive.id}>
              {executive.name}
            </option>
          ))}
        </select>
        <small>Hold Ctrl/Cmd to select multiple executives</small>
      </label>
      <br />

      <div style={{ display: 'flex', gap: '1rem' }}>
        <button type="submit" disabled={loading}>
          {loading ? 'Creating CSO...' : 'Add CSO'}
        </button>
        <button type="button" onClick={cancel}>
          Cancel
        </button>
      </div>
    </form>
  );
}

export default AddCSO;
