// ExecFeed.js
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

export default function ExecFeed() {
  const [posts, setPosts] = useState([]);
  const [profiles, setProfiles] = useState({});
  const [executives, setExecutives] = useState({});
  const [memberFilter, setMemberFilter] = useState("Everyone");
  const [activeTab, setActiveTab] = useState("Post");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      // Fetch posts
      const { data: postsData, error: postsError } = await supabase
        .from("posts")
        .select("*")
        .order("created_at", { ascending: false });

      if (postsError) throw postsError;

      // Fetch all profiles
      const { data: profilesData, error: profilesError } = await supabase
        .from("profiles")
        .select("*");

      if (profilesError) throw profilesError;

      // Fetch all executives
      const { data: executivesData, error: executivesError } = await supabase
        .from("executive")
        .select("*");

      if (executivesError) throw executivesError;

      // Create lookup objects
      const profilesLookup = {};
      profilesData.forEach(profile => {
        profilesLookup[profile.id] = profile;
      });

      const executivesLookup = {};
      executivesData.forEach(exec => {
        executivesLookup[exec.id] = exec;
      });

      setPosts(postsData);
      setProfiles(profilesLookup);
      setExecutives(executivesLookup);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  };

  const formatTimeAgo = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));
    
    if (diffInMinutes < 60) {
      return `${diffInMinutes}m`;
    } else if (diffInMinutes < 1440) {
      return `${Math.floor(diffInMinutes / 60)}h`;
    } else {
      return `${Math.floor(diffInMinutes / 1440)}d`;
    }
  };

  return (
    <main className="exec-feed-container">
      {/* Header */}
      <header className="feed-header">
        <button className="back-button">← Back To Profile</button>
        <section className="cso-info">
          <figure className="cso-logo">
            <span className="logo-placeholder"></span>
          </figure>
          <h1>CSAM</h1>
        </section>
      </header>

      {/* Create Post Section */}
      <section className="create-post-section">
        <input 
          type="text" 
          placeholder="Create a post" 
          className="create-post-input"
          aria-label="Create a new post"
        />
      </section>

      {/* Navigation Tabs */}
      <nav className="nav-tabs" role="tablist">
        <button 
          className={`tab-button ${activeTab === 'Post' ? 'active' : ''}`}
          onClick={() => setActiveTab('Post')}
          role="tab"
          aria-selected={activeTab === 'Post'}
        >
          Post
        </button>
        <button 
          className={`tab-button ${activeTab === 'Images' ? 'active' : ''}`}
          onClick={() => setActiveTab('Images')}
          role="tab"
          aria-selected={activeTab === 'Images'}
        >
          📷 Images
        </button>
        <button 
          className={`tab-button ${activeTab === 'POST FOR' ? 'active' : ''}`}
          onClick={() => setActiveTab('POST FOR')}
          role="tab"
          aria-selected={activeTab === 'POST FOR'}
        >
          📋 POST FOR
        </button>
      </nav>

      {/* Members Filter */}
      <section className="members-filter">
        <label htmlFor="members-select">Members</label>
        <select 
          id="members-select"
          value={memberFilter} 
          onChange={(e) => setMemberFilter(e.target.value)}
          className="members-select"
        >
          <option value="Everyone">Everyone</option>
          <option value="Executives">Executives Only</option>
        </select>
      </section>

      {/* Posts Feed */}
      <section className="posts-feed" role="feed" aria-label="Executive posts">
        {loading ? (
          <p className="loading">Loading posts...</p>
        ) : (
          posts.map((post) => (
            <article key={post.id} className="post-card">
              <header className="post-header">
                <figure className="user-avatar">
                  <img 
                    src={profiles[post.id]?.avatar_url || "/default-avatar.png"} 
                    alt={`${profiles[post.id]?.full_name || "Executive"}'s avatar`}
                  />
                </figure>
                <hgroup className="user-info">
                  <h3>{profiles[post.id]?.full_name || "Executive's name"}</h3>
                  <p className="post-time">
                    <time dateTime={post.created_at}>
                      Time of post · {formatTimeAgo(post.created_at)}
                    </time>
                  </p>
                </hgroup>
              </header>
              
              <section className="post-content">
                <p className="post-caption">{post.caption || "Message or"}</p>
                {post.media_url && post.media_type === 'image' && (
                  <figure className="post-media">
                    <img 
                      src={post.media_url} 
                      alt="Post content" 
                      className="post-image"
                    />
                  </figure>
                )}
                {post.media_url && post.media_type === 'video' && (
                  <figure className="post-media">
                    <video 
                      src={post.media_url} 
                      controls 
                      className="post-video"
                      aria-label="Post video content"
                    />
                  </figure>
                )}
              </section>
            </article>
          ))
        )}
      </section>

      {/* Groups Joined Sidebar */}
      <aside className="groups-sidebar">
        <h3>Groups Joined:</h3>
        <article className="group-item">
          <img src="/wits-logo.png" alt="Wits DevSoc logo" className="group-logo" />
          <section className="group-info">
            <h4>Wits DevSoc</h4>
            <p>Category</p>
            <p className="followers">Number of followers</p>
          </section>
        </article>
      </aside>
    </main>
  );
}
