import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function AddCSO() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    executives: [],
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [executives, setExecutives] = useState([]);

  // Executive search states
  const [executiveSearch, setExecutiveSearch] = useState('');
  const [showExecutiveSuggestions, setShowExecutiveSuggestions] =
    useState(false);
  const [selectedExecutives, setSelectedExecutives] = useState([]);

  // Fetch executives from the database
  useEffect(() => {
    fetchExecutives();
  }, []);

  const fetchExecutives = async () => {
    try {
      const { data: execData, error: execError } = await supabase
        .from('executive')
        .select('id, "student/staff_number"');

      if (execError) throw execError;

      if (execData && execData.length > 0) {
        const studentNumbers = execData
          .map((exec) => exec['student/staff_number'])
          .filter(Boolean);

        if (studentNumbers.length > 0) {
          const { data: profileData, error: profileError } = await supabase
            .from('profiles')
            .select('id, full_name')
            .in('id', studentNumbers);

          if (profileError) throw profileError;

          const executivesWithNames = execData.map((exec) => {
            const profile = profileData?.find(
              (p) => p.id === exec['student/staff_number']
            );
            return {
              id: exec.id,
              student_staff_number: exec['student/staff_number'],
              name: profile?.full_name || 'Unknown Executive',
            };
          });

          setExecutives(executivesWithNames);
        }
      }
    } catch (err) {
      console.error('Error fetching executives:', err);
      setError('Failed to load executives');
    }
  };

  // Filter executives based on search
  const filteredExecutives = executives.filter(
    (exec) =>
      exec.name.toLowerCase().includes(executiveSearch.toLowerCase()) &&
      !selectedExecutives.find((selected) => selected.id === exec.id)
  );

  const handleAddExecutive = (executive) => {
    setSelectedExecutives([...selectedExecutives, executive]);
    setFormData({
      ...formData,
      executives: [...formData.executives, executive.id],
    });
    setExecutiveSearch('');
    setShowExecutiveSuggestions(false);
  };

  const handleRemoveExecutive = (executiveId) => {
    setSelectedExecutives(
      selectedExecutives.filter((exec) => exec.id !== executiveId)
    );
    setFormData({
      ...formData,
      executives: formData.executives.filter((id) => id !== executiveId),
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        setError('Please select an image file');
        return;
      }
      if (file.size > 5 * 1024 * 1024) {
        setError('File size must be less than 5MB');
        return;
      }
      setLogoFile(file);
      setError(null);
    }
  };

  const uploadLogo = async (file) => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Math.random()}.${fileExt}`;
    const filePath = `cso-logos/${fileName}`;

    const { data, error } = await supabase.storage
      .from('media')
      .upload(filePath, file);

    if (error) {
      throw error;
    }

    const {
      data: { publicUrl },
    } = supabase.storage.from('media').getPublicUrl(filePath);

    return publicUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      if (!formData.name) {
        throw new Error('CSO name is required');
      }
      if (!formData.cluster) {
        throw new Error('Cluster selection is required');
      }

      let logo_url = null;

      if (logoFile) {
        logo_url = await uploadLogo(logoFile);
      }

      // First, check if the user has permission to insert into the cso table
      const { data: user } = await supabase.auth.getUser();

      if (!user) {
        throw new Error('You must be logged in to create a CSO');
      }

      // If RLS is enabled, you might need to use a database function (RPC) instead
      // This is a common pattern to bypass RLS for specific operations
      const { data: csoData, error: csoError } = await supabase.rpc(
        'create_cso',
        {
          p_name: formData.name,
          p_description: formData.description,
          p_cluster: formData.cluster,
          p_subscription: formData.subscription,
          p_logo_url: logo_url,
        }
      );

      if (csoError) throw csoError;

      if (formData.executives.length > 0 && csoData && csoData.length > 0) {
        const executivesToLink = formData.executives.map((execId) => ({
          cso_id: csoData[0].id,
          exec_id: execId,
          portfolio: 'Member',
          can_post: true,
          start_date: new Date().toISOString().split('T')[0],
        }));

        const { error: linkError } = await supabase
          .from('cso_exec')
          .insert(executivesToLink);

        if (linkError) throw linkError;
      }

      setSuccess(true);
      setFormData({
        name: '',
        description: '',
        cluster: '',
        subscription: '',
        executives: [],
      });
      setSelectedExecutives([]);
      setLogoFile(null);

      const fileInput = document.getElementById('logo-input');
      if (fileInput) fileInput.value = '';

      setTimeout(() => {
        navigate('/CSO');
      }, 2000);
    } catch (err) {
      setError(err.message);
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const cancel = () => {
    navigate('/CSO');
  };

  return (
    <article className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <header className="mb-6">
        <h1 className="text-2xl font-bold text-gray-800">
          Add a Club, Society, or Organization
        </h1>
      </header>

      <form onSubmit={handleSubmit} className="space-y-6">
        {error && (
          <section
            className="p-4 bg-red-50 border border-red-200 rounded-md"
            role="alert"
          >
            <p className="text-red-700">Error: {error}</p>
          </section>
        )}

        {success && (
          <section
            className="p-4 bg-green-50 border border-green-200 rounded-md"
            role="status"
          >
            <p className="text-green-700">
              CSO created successfully! Redirecting...
            </p>
          </section>
        )}

        <fieldset className="p-4 border border-gray-200 rounded-md">
          <legend className="text-lg font-medium text-gray-700 px-2">
            CSO Information
          </legend>

          <div className="mb-4">
            <label
              htmlFor="logo-input"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Logo
            </label>
            <input
              id="logo-input"
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
          </div>

          <div className="mb-4">
            <label
              htmlFor="cso-name"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              CSO Name *
            </label>
            <input
              id="cso-name"
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div className="mb-4">
            <label
              htmlFor="cso-description"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Description
            </label>
            <textarea
              id="cso-description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="3"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div className="mb-4">
            <label
              htmlFor="cso-cluster"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Cluster *
            </label>
            <select
              id="cso-cluster"
              name="cluster"
              value={formData.cluster}
              onChange={handleChange}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select a cluster...</option>
              <option value="political">Political Cluster</option>
              <option value="academic">Academic Cluster</option>
              <option value="social">Social Cluster</option>
              <option value="cultural">Cultural Cluster</option>
              <option value="religious">Religious Cluster</option>
              <option value="sport">WITS Sport</option>
              <option value="council">School Council</option>
            </select>
          </div>
        </fieldset>

        <fieldset className="p-4 border border-gray-200 rounded-md">
          <legend className="text-lg font-medium text-gray-700 px-2">
            Subscription
          </legend>
          <div className="mb-4">
            <p className="block text-sm font-medium text-gray-700 mb-2">
              Subscription Required
            </p>
            <div className="flex space-x-4">
              <label htmlFor="subscription-no" className="flex items-center">
                <input
                  id="subscription-no"
                  type="radio"
                  name="subscription"
                  value="no"
                  checked={formData.subscription === 'no'}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500"
                />
                <span className="ml-2 text-gray-700">No</span>
              </label>
              <label htmlFor="subscription-yes" className="flex items-center">
                <input
                  id="subscription-yes"
                  type="radio"
                  name="subscription"
                  value="yes"
                  checked={formData.subscription === 'yes'}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500"
                />
                <span className="ml-2 text-gray-700">Yes</span>
              </label>
            </div>
          </div>
        </fieldset>

        <fieldset className="p-4 border border-gray-200 rounded-md">
          <legend className="text-lg font-medium text-gray-700 px-2">
            Executive Members
          </legend>
          <div className="mb-4">
            <label
              htmlFor="executive-search"
              className="block text-sm font-medium text-gray-700 mb-1"
            >
              Executive(s)
            </label>
            <div className="relative">
              <input
                id="executive-search"
                type="text"
                placeholder="Type to search executives..."
                value={executiveSearch}
                onChange={(e) => {
                  setExecutiveSearch(e.target.value);
                  setShowExecutiveSuggestions(true);
                }}
                onFocus={() => setShowExecutiveSuggestions(true)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />

              {/* Suggestions dropdown */}
              {showExecutiveSuggestions &&
                executiveSearch &&
                filteredExecutives.length > 0 && (
                  <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-auto">
                    {filteredExecutives.map((exec) => (
                      <div
                        key={exec.id}
                        onClick={() => handleAddExecutive(exec)}
                        className="px-4 py-2 cursor-pointer hover:bg-blue-50"
                      >
                        {exec.name}
                      </div>
                    ))}
                  </div>
                )}

              {/* Click outside to close */}
              {showExecutiveSuggestions && (
                <div
                  className="fixed inset-0 z-0"
                  onClick={() => setShowExecutiveSuggestions(false)}
                />
              )}
            </div>

            {/* Selected executives */}
            {selectedExecutives.length > 0 && (
              <section className="mt-4">
                <h3 className="text-sm font-medium text-gray-700 mb-2">
                  Selected executives:
                </h3>
                <div className="flex flex-wrap gap-2">
                  {selectedExecutives.map((exec) => (
                    <div
                      key={exec.id}
                      className="flex items-center bg-gray-100 px-3 py-1 rounded-full"
                    >
                      <span className="text-sm text-gray-700">{exec.name}</span>
                      <button
                        type="button"
                        onClick={() => handleRemoveExecutive(exec.id)}
                        aria-label={`Remove ${exec.name}`}
                        className="ml-2 text-gray-500 hover:text-gray-700"
                      >
                        ×
                      </button>
                    </div>
                  ))}
                </div>
              </section>
            )}
          </div>
        </fieldset>

        <div className="flex space-x-4">
          <button
            type="submit"
            disabled={loading}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {loading ? 'Creating CSO...' : 'Add CSO'}
          </button>
          <button
            type="button"
            onClick={cancel}
            className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-gray-500"
          >
            Cancel
          </button>
        </div>
      </form>
    </article>
  );
}

export default AddCSO;
