import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function ExecutiveSearch({ executives, selectedExecutives, onAdd, onRemove }) {
  const [search, setSearch] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);

  const filteredExecutives = executives.filter(
    exec =>
      exec.name.toLowerCase().includes(search.toLowerCase()) &&
      !selectedExecutives.find(selected => selected.id === exec.id)
  );

  const handleAdd = executive => {
    onAdd(executive);
    setSearch('');
    setShowSuggestions(false);
  };

  return (
    <section>
      <div role="search">
        <input
          type="search"
          placeholder="Type to search executives..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          onFocus={() => setShowSuggestions(true)}
          aria-label="Search executives"
        />

        {showSuggestions && search && filteredExecutives.length > 0 && (
          <div className="suggestions-container">
            <ul className="suggestions-dropdown" role="listbox">
              {filteredExecutives.map(exec => (
                <li
                  key={exec.id}
                  onClick={() => handleAdd(exec)}
                  role="option"
                  tabIndex={0}
                  onKeyDown={e => e.key === 'Enter' && handleAdd(exec)}
                >
                  {exec.name}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      {selectedExecutives.length > 0 && (
        <section aria-label="Selected executives">
          <h3>Selected executives:</h3>
          <ul className="executive-chips">
            {selectedExecutives.map(exec => (
              <li key={exec.id} className="chip">
                {exec.name}
                <button
                  type="button"
                  onClick={() => onRemove(exec.id)}
                  aria-label={`Remove ${exec.name}`}
                >
                  Ã—
                </button>
              </li>
            ))}
          </ul>
        </section>
      )}
    </section>
  );
}

function AddCSO() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    executives: [],
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState({ type: null, message: null });
  const [executives, setExecutives] = useState([]);
  const [selectedExecutives, setSelectedExecutives] = useState([]);

  useEffect(() => {
    fetchExecutives();
  }, []);

  const fetchExecutives = async () => {
    try {
      const { data: execData, error: execError } = await supabase
        .from('executive')
        .select('id, student/staff_number');

      if (execError) throw execError;

      if (execData?.length > 0) {
        const studentNumbers = execData
          .map(exec => exec['student/staff_number'])
          .filter(Boolean);

        if (studentNumbers.length > 0) {
          const { data: profileData, error: profileError } = await supabase
            .from('profiles')
            .select('id, full_name')
            .in('id', studentNumbers);

          if (profileError) throw profileError;

          const executivesWithNames = execData.map(exec => {
            const profile = profileData?.find(
              p => p.id === exec['student/staff_number']
            );
            return {
              id: exec.id,
              name: profile?.full_name || 'Unknown Executive',
            };
          });

          setExecutives(executivesWithNames);
        }
      }
    } catch (err) {
      console.error('Error fetching executives:', err);
      setStatus({ type: 'error', message: 'Failed to load executives' });
    }
  };

  const handleAddExecutive = executive => {
    setSelectedExecutives([...selectedExecutives, executive]);
    setFormData(prev => ({
      ...prev,
      executives: [...prev.executives, executive.id],
    }));
  };

  const handleRemoveExecutive = executiveId => {
    setSelectedExecutives(prev => prev.filter(exec => exec.id !== executiveId));
    setFormData(prev => ({
      ...prev,
      executives: prev.executives.filter(id => id !== executiveId),
    }));
  };

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = e => {
    const file = e.target.files[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      setStatus({ type: 'error', message: 'Please select an image file' });
      return;
    }
    if (file.size > 5 * 1024 * 1024) {
      setStatus({ type: 'error', message: 'File size must be less than 5MB' });
      return;
    }
    setLogoFile(file);
    setStatus({ type: null, message: null });
  };

  const uploadLogo = async file => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Date.now()}-${Math.random()}.${fileExt}`;

    const { error } = await supabase.storage
      .from('cso-logos')
      .upload(fileName, file);

    if (error) throw error;

    const { data: { publicUrl } } = supabase.storage
      .from('cso-logos')
      .getPublicUrl(fileName);

    return publicUrl;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setStatus({ type: null, message: null });

    try {
      if (!formData.name) throw new Error('CSO name is required');
      if (!formData.cluster) throw new Error('Cluster selection is required');

      let logo_url = null;
      if (logoFile) {
        logo_url = await uploadLogo(logoFile);
      }

      const { data: csoData, error: csoError } = await supabase
        .from('cso')
        .insert([{ ...formData, logo_url }])
        .select()
        .single();

      if (csoError) throw csoError;

      if (formData.executives.length > 0) {
        const executivesToLink = formData.executives.map(execId => ({
          cso_id: csoData.id,
          exec_id: execId,
          portfolio: 'Member',
          can_post: true,
          start_date: new Date().toISOString().split('T')[0],
        }));

        const { error: linkError } = await supabase
          .from('cso_exec')
          .insert(executivesToLink);

        if (linkError) {
          await supabase.from('cso').delete().eq('id', csoData.id);
          throw linkError;
        }
      }

      setStatus({ type: 'success', message: 'CSO created successfully!' });

      // Reset form
      setFormData({
        name: '',
        description: '',
        cluster: '',
        subscription: '',
        executives: [],
      });
      setSelectedExecutives([]);
      setLogoFile(null);
      document.getElementById('logo-input').value = '';

      setTimeout(() => navigate('/CSO'), 2000);
    } catch (err) {
      setStatus({
        type: 'error',
        message: err.message || 'An unexpected error occurred',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="add-cso-container">
      <header>
        <h1>Add a Club, Society, or Organization</h1>
      </header>

      {status.message && (
        <div role="alert" className={`status-message ${status.type}`}>
          {status.message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="cso-form">
        <fieldset>
          <legend>CSO Information</legend>
          
          <div>
            <label htmlFor="logo-input">Logo</label>
            <input
              id="logo-input"
              type="file"
              accept="image/*"
              onChange={handleFileChange}
            />
            <small>Upload an image file (max 5MB)</small>
          </div>

          <div>
            <label htmlFor="name">
              CSO Name <span aria-hidden="true">*</span>
            </label>
            <input
              id="name"
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="3"
            />
          </div>

          <div>
            <label htmlFor="cluster">
              Cluster <span aria-hidden="true">*</span>
            </label>
            <select
              id="cluster"
              name="cluster"
              value={formData.cluster}
              onChange={handleChange}
              required
            >
              <option value="">Select a cluster...</option>
              <option value="political">Political Cluster</option>
              <option value="academic">Academic Cluster</option>
              <option value="social">Social Cluster</option>
              <option value="cultural">Cultural Cluster</option>
              <option value="religious">Religious Cluster</option>
              <option value="sport">WITS Sport</option>
              <option value="council">School Council</option>
            </select>
          </div>
        </fieldset>

        <fieldset>
          <legend>Subscription Required</legend>
          <div>
            <label>
              <input
                type="radio"
                name="subscription"
                value="no"
                checked={formData.subscription === 'no'}
                onChange={handleChange}
              />
              No
            </label>
            <label>
              <input
                type="radio"
                name="subscription"
                value="yes"
                checked={formData.subscription === 'yes'}
                onChange={handleChange}
              />
              Yes
            </label>
          </div>
        </fieldset>

        <fieldset>
          <legend>Executive Team</legend>
          <ExecutiveSearch
            executives={executives}
            selectedExecutives={selectedExecutives}
            onAdd={handleAddExecutive}
            onRemove={handleRemoveExecutive}
          />
        </fieldset>

        <div className="form-actions">
          <button type="submit" disabled={loading}>
            {loading ? 'Creating CSO...' : 'Add CSO'}
          </button>
          <button type="button" onClick={() => navigate('/CSO')}>
            Cancel
          </button>
        </div>
      </form>
    </main>
  );
}

export default AddCSO;
