import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

function AddCSO() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    cluster: '',
    subscription: '',
    executives: [],
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [executives, setExecutives] = useState([]);

  // Executive search states
  const [executiveSearch, setExecutiveSearch] = useState('');
  const [showExecutiveSuggestions, setShowExecutiveSuggestions] =
    useState(false);
  const [selectedExecutives, setSelectedExecutives] = useState([]);

  // Fetch executives from the database
  useEffect(() => {
    fetchExecutives();
  }, []);

  const fetchExecutives = async () => {
    try {
      const { data: execData, error: execError } = await supabase
        .from('executive')
        .select('id, "student/staff_number"');

      if (execError) throw execError;

      if (execData && execData.length > 0) {
        const studentNumbers = execData
          .map((exec) => exec['student/staff_number'])
          .filter(Boolean);

        if (studentNumbers.length > 0) {
          const { data: profileData, error: profileError } = await supabase
            .from('profiles')
            .select('id, full_name')
            .in('id', studentNumbers);

          if (profileError) throw profileError;

          const executivesWithNames = execData.map((exec) => {
            const profile = profileData?.find(
              (p) => p.id === exec['student/staff_number']
            );
            return {
              id: exec.id,
              student_staff_number: exec['student/staff_number'],
              name: profile?.full_name || 'Unknown Executive',
            };
          });

          setExecutives(executivesWithNames);
        }
      }
    } catch (err) {
      console.error('Error fetching executives:', err);
      setError('Failed to load executives');
    }
  };

  // Filter executives based on search
  const filteredExecutives = executives.filter(
    (exec) =>
      exec.name.toLowerCase().includes(executiveSearch.toLowerCase()) &&
      !selectedExecutives.find((selected) => selected.id === exec.id)
  );

  const handleAddExecutive = (executive) => {
    setSelectedExecutives([...selectedExecutives, executive]);
    setFormData({
      ...formData,
      executives: [...formData.executives, executive.id],
    });
    setExecutiveSearch('');
    setShowExecutiveSuggestions(false);
  };

  const handleRemoveExecutive = (executiveId) => {
    setSelectedExecutives(
      selectedExecutives.filter((exec) => exec.id !== executiveId)
    );
    setFormData({
      ...formData,
      executives: formData.executives.filter((id) => id !== executiveId),
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        setError('Please select an image file');
        return;
      }
      if (file.size > 5 * 1024 * 1024) {
        setError('File size must be less than 5MB');
        return;
      }
      setLogoFile(file);
      setError(null);
    }
  };

  const uploadLogo = async (file) => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Math.random()}.${fileExt}`;
    const filePath = `cso-logos/${fileName}`;

    const { data, error } = await supabase.storage
      .from('media')
      .upload(filePath, file);

    if (error) {
      throw error;
    }

    const {
      data: { publicUrl },
    } = supabase.storage.from('media').getPublicUrl(filePath);

    return publicUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      if (!formData.name) {
        throw new Error('CSO name is required');
      }
      if (!formData.cluster) {
        throw new Error('Cluster selection is required');
      }

      let logo_url = null;

      if (logoFile) {
        logo_url = await uploadLogo(logoFile);
      }

      const { data: csoData, error: csoError } = await supabase
        .from('cso')
        .insert([
          {
            name: formData.name,
            description: formData.description,
            cluster: formData.cluster,
            subscription: formData.subscription,
            logo_url: logo_url,
          },
        ])
        .select()
        .single();

      if (csoError) throw csoError;

      if (formData.executives.length > 0) {
        const executivesToLink = formData.executives.map((execId) => ({
          cso_id: csoData.id,
          exec_id: execId,
          portfolio: 'Member',
          can_post: true,
          start_date: new Date().toISOString().split('T')[0],
        }));

        const { error: linkError } = await supabase
          .from('cso_exec')
          .insert(executivesToLink);

        if (linkError) throw linkError;
      }

      setSuccess(true);
      setFormData({
        name: '',
        description: '',
        cluster: '',
        subscription: '',
        executives: [],
      });
      setSelectedExecutives([]);
      setLogoFile(null);

      const fileInput = document.getElementById('logo-input');
      if (fileInput) fileInput.value = '';

      setTimeout(() => {
        navigate('/CSO');
      }, 2000);
    } catch (err) {
      setError(err.message);
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const cancel = () => {
    navigate('/CSO');
  };

  return (
    <article className="cso-form-container">
      <header>
        <h1>Add a Club, Society, or Organization</h1>
      </header>

      <form onSubmit={handleSubmit} className="cso-form">
        {error && (
          <section className="error-message" role="alert">
            <p>Error: {error}</p>
          </section>
        )}

        {success && (
          <section className="success-message" role="status">
            <p>CSO created successfully!</p>
          </section>
        )}

        <fieldset>
          <legend>CSO Information</legend>

          <div className="form-group">
            <label htmlFor="logo-input">
              Logo
              <input
                id="logo-input"
                type="file"
                accept="image/*"
                onChange={handleFileChange}
              />
            </label>
          </div>

          <div className="form-group">
            <label htmlFor="cso-name">
              CSO Name
              <input
                id="cso-name"
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </label>
          </div>

          <div className="form-group">
            <label htmlFor="cso-description">
              Description
              <textarea
                id="cso-description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="3"
              />
            </label>
          </div>

          <div className="form-group">
            <label htmlFor="cso-cluster">
              Cluster
              <select
                id="cso-cluster"
                name="cluster"
                value={formData.cluster}
                onChange={handleChange}
                required
              >
                <option value="">Select a cluster...</option>
                <option value="political">Political Cluster</option>
                <option value="academic">Academic Cluster</option>
                <option value="social">Social Cluster</option>
                <option value="cultural">Cultural Cluster</option>
                <option value="religious">Religious Cluster</option>
                <option value="sport">WITS Sport</option>
                <option value="council">School Council</option>
              </select>
            </label>
          </div>
        </fieldset>

        <fieldset>
          <legend>Subscription</legend>
          <div className="form-group">
            <p>Subscription Required</p>
            <div className="radio-group">
              <label htmlFor="subscription-no">
                <input
                  id="subscription-no"
                  type="radio"
                  name="subscription"
                  value="no"
                  checked={formData.subscription === 'no'}
                  onChange={handleChange}
                />
                No
              </label>
              <label htmlFor="subscription-yes">
                <input
                  id="subscription-yes"
                  type="radio"
                  name="subscription"
                  value="yes"
                  checked={formData.subscription === 'yes'}
                  onChange={handleChange}
                />
                Yes
              </label>
            </div>
          </div>
        </fieldset>

        <fieldset>
          <legend>Executive Members</legend>
          <div className="form-group">
            <label htmlFor="executive-search">
              Executive(s)
              <div className="search-container">
                <input
                  id="executive-search"
                  type="text"
                  placeholder="Type to search executives..."
                  value={executiveSearch}
                  onChange={(e) => {
                    setExecutiveSearch(e.target.value);
                    setShowExecutiveSuggestions(true);
                  }}
                  onFocus={() => setShowExecutiveSuggestions(true)}
                />

                {/* Suggestions dropdown */}
                {showExecutiveSuggestions &&
                  executiveSearch &&
                  filteredExecutives.length > 0 && (
                    <div className="suggestions-dropdown">
                      {filteredExecutives.map((exec) => (
                        <div
                          key={exec.id}
                          onClick={() => handleAddExecutive(exec)}
                          className="suggestion-item"
                        >
                          {exec.name}
                        </div>
                      ))}
                    </div>
                  )}

                {/* Click outside to close */}
                {showExecutiveSuggestions && (
                  <div
                    className="click-outside-overlay"
                    onClick={() => setShowExecutiveSuggestions(false)}
                  />
                )}
              </div>
            </label>

            {/* Selected executives */}
            {selectedExecutives.length > 0 && (
              <section className="selected-executives">
                <h3>Selected executives:</h3>
                <div className="executive-tags">
                  {selectedExecutives.map((exec) => (
                    <div key={exec.id} className="executive-tag">
                      <span>{exec.name}</span>
                      <button
                        type="button"
                        onClick={() => handleRemoveExecutive(exec.id)}
                        aria-label={`Remove ${exec.name}`}
                      >
                        ×
                      </button>
                    </div>
                  ))}
                </div>
              </section>
            )}
          </div>
        </fieldset>

        <div className="form-actions">
          <button type="submit" disabled={loading}>
            {loading ? 'Creating CSO...' : 'Add CSO'}
          </button>
          <button type="button" onClick={cancel}>
            Cancel
          </button>
        </div>
      </form>
    </article>
  );
}

export default AddCSO;
